var merge = function(intervals) {
    let results = [];
    let len = intervals.length;
    if(len === 0) return [];

    intervals.sort((a, b) => a[0] - b[0]);

    let i = 0;
    while(i = intervals[i + 1][0]) {
        i ++;
        currRight = Math.max(intervals[i][1], currRight)
      }
      results.push([currLeft, currRight]);
      i++;
    }

    return results;
};

