var merge = function(intervals) {
  const arr = []
  let now = intervals.shift()
  let prev
  while (now) {
    if (!prev) {
      prev = now
      continue
    }

    if (prev[1] >= now[0]) {
      prev = [
          prev[0] = now[1] ? prev[1] : now[1] 
      ]
    } else {
      arr.push(prev)
      prev = now
    }

    now = intervals.shift()
    if (!now) {
      arr.push(prev)
    }
  }
  return arr    
};

