        /**
            React 利用 fiber 数据结构将渲染分成多个任务每次执行一个
        */
        // 获取一个渲染任务
        const unitOfWork = getUnitOfWork()
        // 执行渲染任务
        excuteUnitOfWork(unitOfWork)
    }
    window.requestIdleCallback(callback)
}

