const curry = func => {
  function next(...args) {
    if (func.length === args.length) {
      return func(...args);
    }
    return function(...newArgs) {
      return next(...args, ...newArgs);
    };
  }
  return next;
};

const fun = (a, b, c) => {
  return a + b + c;
};

console.log(curry(fun)(1)(2)(3));
// => 6
console.log(curry(fun)(1, 2, 3));
// => 6
