var moveZeroes = function(nums) {
    // 反向遍历nums，这样将 0 移到数组后面时，下一个 i 值对应的数就不会乱
    // 循环跳过最后一个，因为如果是 0, 它也应该在最后一位
    for (let i = nums.length-1; i > -1; --i) {
        if (nums[i] === 0) {
            nums.push(
                nums.splice(i, 1)[0]
            )
        }
    }
    return nums
};
