柯里化实现

const curry = func => {
  const loop = (...args) => {
    return args.length === func.length
      ? func(...args)
      : (...newArgs) => loop(...args, ...newArgs)
  }
  return loop
}

const func = (a,b,c) => {
  return a+b+c
}

curry(func)(1)(2)(3)
// => 6
curry(func)(1,2,3)
// => 6
curry(func)(1,2)(3)
// => 6

