}

function Son(props) {
  // 执行 Father，改变 Father 的 this 指向，继承 Father 的属性
  Father.call(this, props)
}

// Son 原型指向 Father 实例，继承 say 方法
Son.prototype = new Father()

var s = new Son("John")

s.say() // my name is John

