var arr1 = [1,2,3,4]
var arr2 = [3,4,5]

var mark = function(arrs) {
  var o = {}
  arrs.forEach(arr => {
    arr.forEach(item => {
      if (o[item]) {
        o[item] += 1
      } else {
        o[item] = 1
      }
    });
  });
  return o
}

// 交集
var innersection = function(arrs) {
  var o = mark(arrs)
  return Object.keys(o).filter(key => o[key]>1).map(key => key)
}
// 并集
var union = function(arrs) {
  var o = mark(arrs)
  return Object.keys(o).map(key => key)
}
