var N = function(Func) {
  return function() {
    // 定义一个用于返回的对象
    var obj = {};
    // 将构造函数的prototype赋值给obj对象的__proto__属性
    obj.__proto__ = Func.prototype;
    // 将构造函数的this重定向到obj对象中来
    Func.apply(obj, arguments);
    // 返回obj对象
    return obj
  }
}

