var singleNonDuplicate = function(nums) {
    const obj = {};
    let res;
    nums.forEach(i => {
        if (typeof obj[i] === 'undefined') {
            obj[i] = 1;
        } else {
            obj[i] += 1;
        }
    })
    Object.keys(obj).forEach(l => {
        if (obj[l] === 1) {
            res = l
        }
    })
    return res
};
