const moveZero = (arr) =>{
  let len = arr.length
  return arr.filter(item=>item===0?len--&&false:true).concat(Array(arr.length-len).fill(0))
}
moveZero([0,0,1,4,0,2])  //[1,4,2,0,0,0]

