const isArray = obj => Object.prototype.toString.call(obj) === '[object Array]';

const deepClone = obj => {
  if(typeof obj !== 'object') {
    return obj;
  }
  const o = isArray(obj) ? [] : {}
  for(let key in obj) {
    if(obj.hasOwnProperty(key)) {
      o[key] = typeof obj[key] === 'object' ? deepClone(obj[key]) : obj[key]; 
    }
  }
  return o;
}

